"use strict";

// src/constants.ts
var dataExpiryTime = 60 * 60 * 1e3;

// src/background/toggle-icon.ts
var setColorScheme = (perfersDark2) => {
  void chrome.runtime.sendMessage({ ["colorSchemes" /* colorScheme */]: perfersDark2 ? "dark" : "light" });
};
var perfersDark = window.matchMedia("(prefers-color-scheme: dark)");
setColorScheme(perfersDark.matches);
perfersDark.addEventListener("change", ({ matches }) => {
  setColorScheme(matches);
});
